import React, { useState, useEffect } from 'react';
import api from '../api'; // Make sure this is correctly set up to point to your API

const Books = ({ token }) => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    // It's assumed 'api' is set up to correctly point to your Flask backend
    api.get('/books', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(response => {
      // Make sure response.data is the expected format of an array of book objects
      setBooks(response.data);
    })
    .catch(error => {
      // It's good practice to handle errors in case of API call failures
      console.error('There was an error fetching the books!', error);
    });
  }, [token]); // Dependency on 'token' to refetch books when it changes

  return (
    <div className="books-container">
      <h1>Books</h1>
      <ul className="books-list">
        {books.map(book => (
          <li key={book.key} className="book-item"> {/* Ensure 'book.key' is unique */}
            <div className="book-title">{book.key}</div>
            <a href={book.url} target="_blank" rel="noopener noreferrer" className="download-link">
              Download
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Books;
