import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import api from '../api';


const Books = ({ token }) => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    axios.get('/books', { // Adjust URL/port as per your Flask app
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(response => {
      setBooks(response.data);
    })
    .catch(error => {
      console.error('There was an error fetching the books!', error);
    });
  }, [token]); // Dependency array to refetch when token changes

  return (
    <div className="books-container">
      <h1>Books</h1>
      <ul className="books-list">
        {books.map(book => (
          <li key={book.key} className="book-item">
            <div className="book-title">{book.key}</div>
            <a href={book.url} target="_blank" rel="noopener noreferrer" className="download-link">
              Download
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Books;
