import React, { useState, useEffect } from 'react';
import api from '../api';
import './Books.css';

const Books = ({ token }) => {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        api.get('/books', {
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
        .then(response => {
            setBooks(response.data);
        })
        .catch(error => {
            console.error('There was an error fetching the books!', error);
        });
    }, [token]);

    return (
        React.createElement('div', { className: 'books-container' }, 
            React.createElement('h1', { className: 'books-title' }, 'Books'),
            React.createElement('ul', { className: 'books-list' }, 
                books.map(book => 
                    React.createElement('li', { key: book.id, className: 'book-item' }, book.title)
                )
            )
        )
    );
};

export default Books;
